# Create processed data in the required format
processed_data = {
    "id": str(uuid.uuid4()),
    "projectId": "project-capstone",
    "sourceIds": enriched_data.select("id").distinct().limit(10).rdd.flatMap(lambda x: x).collect() or ["sample-relationship-data"],
    "analysisType": "relationship",
    "title": "Business Metrics Relationship Analysis",
    "description": "Analysis of relationships and correlations between key business metrics",
    "data": {
        "type": "relationship_network",
        "value": {
            "correlations": correlations,
            "lagged_correlations": lagged_correlations,
            "network": {
                "nodes": nodes,
                "links": [
                    {
                        "source": corr["source"], 
                        "target": corr["target"], 
                        "value": corr["strength"],
                        "type": corr["relationship_type"]
                    } 
                    for corr in correlations + lagged_correlations
                ]
            },
            "time_series": [
                {
                    "date": str(row.date),
                    "sales": row.daily_sales,
                    "traffic": row.daily_traffic,
                    "inventory": row.avg_inventory_level,
                    "energy": row.daily_energy_usage
                }
                for row in time_series
            ]
        },
        "confidence": 0.85
    },
    "insights": [
        {
            "type": "operational_improvement",
            "title": "Foot Traffic â†’ Sales Relationship",
            "description": f"Foot traffic is a {max(lagged_correlations, key=lambda x: x['strength'])['direction']} leading indicator of sales with {max(lagged_correlations, key=lambda x: x['strength'])['strength'] * 100:.1f}% correlation",
            "impact": 15000,
            "confidence": 0.8
        },
        {
            "type": "cost_reduction",
            "title": "Inventory Optimization Opportunity",
            "description": f"The {min(correlations, key=lambda x: x['correlation'] if 'Inventory' in x['source'] or 'Inventory' in x['target'] else 1)['direction']} correlation between inventory and sales suggests potential for just-in-time inventory management",
            "impact": 8500,
            "confidence": 0.75
        }
    ],
    "visualizationType": "graph",
    "visualizationConfig": {
        "graphType": "network",
        "nodeTypes": ["financial", "customer", "operational", "facility"],
        "linkTypes": ["correlation", "leading_indicator", "weak_correlation"]
    },
    "processedAt": now,
    "createdAt": now
